/*-----Exercice 6-----*/

interface Forme {
    hauteur: number;
    largeur: number;
}

class Rectangle implements Forme {
    constructor(public hauteur: number, public largeur: number) { }

    calculerSurface(): number {
        return this.hauteur * this.largeur;
    }
}

const monRectangle = new Rectangle(5, 10);


const surface = monRectangle.calculerSurface();
console.log(surface);


