const resultat = addition(a, b);
console.log(resultat); 


/*----- Exercice 2-----*/

function salutation(nom: string, titre?: string): string {
    const titreUtilise = titre || "M."; 
    return `Bonjour, ${titreUtilise} ${nom} !`;
}

const nom1 = "Kan";
const salutation1 = salutation(nom1);
console.log(salutation1); 

const nom2 = "Guigui";
const titre2 = "Mme";
const salutation2 = salutation(nom2, titre2);
console.log(salutation2); 

/*----- Exercice 3-----*/

function calculPrixTotal(prixUnitaire: number = 0, quantite: number = 0): number {
    return prixUnitaire * quantite;
}

const prix = 10;
const quantite = 5;
const prixTotal = calculPrixTotal(prix, quantite);
console.log(prixTotal); 

const prix1 = 2.5;
const quantite1 = 8;
const prixTotal1 = calculPrixTotal(prix1, quantite1);
console.log(prixTotal1); 

const prixTotal2 = calculPrixTotal();
console.log(prixTotal2); 


/*----- Exercice 4-----*/

function afficherInfo(parametre: string): void;
function afficherInfo(parametre: number): void;
function afficherInfo(parametre: string | number): void {
    if (typeof parametre === "string") {
        console.log(parametre);
    } else if (typeof parametre === "number") {
        const resultat = parametre * 2;
        console.log(resultat);
    }
}

afficherInfo("hola"); 
afficherInfo(5); 

/*----- Exercice 5-----*/

function renverser<T>(arr: T[]): T[] {
    return arr.reverse();
}

const numbers: number[] = [1, 2, 3, 4, 5];
const reversedNumbers = renverser(numbers);
console.log(reversedNumbers);

const strings: string[] = ['hola', 'tout le monde'];
const reversedStrings = renverser(strings);
console.log(reversedStrings);


